import { useState } from 'react';
import { SuggestionsListComponent } from '../components/SuggestionsListComponent'


export const Autocomplete = ({ suggestions, callBack, childFunc }) => {

    const [searchInput, setSearchInput] = useState("");
    const [filteredSuggestions, setFilteredSuggestions] = useState([]);
    // const [currenSuggestion, setCurrenSuggestion] = useState("");
    //display a new list of suggestions to the user
    const [activeSuggestionIndex, setActiveSuggestionIndex] = useState(0);
    //display the list with suggestions to the user
    const [showSuggestions, setShowSuggestions] = useState(false);

    //when the user changes the input value this method will fire
    const handleInput = (e) => {
        e.preventDefault()
        //what the user types in the searchbar
        const userInput = e.target.value;
        setSearchInput(userInput);

        // Filter suggestions that don't contain the user's input
        const unLinked = suggestions.filter(
            (suggestion) =>
                suggestion.toLowerCase().indexOf(userInput.toLowerCase()) > -1
        );

        callBack(userInput);

        setFilteredSuggestions(unLinked)
        setActiveSuggestionIndex(0)
        setShowSuggestions(true)
    }


    //event that will be emitted when a user clicks on a suggestion
    const setSuggestion = (suggestion) => {
        setFilteredSuggestions([]);

        setSearchInput(suggestion)
        callBack(suggestion);

        setActiveSuggestionIndex(0)
        setShowSuggestions(false)
    }



    const onKeyDown = (e) => {
        if (e.keyCode === 13) {
            //if key is enter keys
            const suggestion = filteredSuggestions[activeSuggestionIndex]
            setSuggestion(suggestion)

        } else if (e.keyCode === 38) {
            //if key is arrow up
            if (activeSuggestionIndex <= 0) {
                return
            } if (activeSuggestionIndex > 0) {
                setActiveSuggestionIndex(activeSuggestionIndex - 1)
            }
        } else if (e.keyCode === 40) {
            //if key is arrow down
            if (activeSuggestionIndex <= filteredSuggestions.length) {
                setActiveSuggestionIndex(activeSuggestionIndex + 1)
            }
        }
    }

    return (
        <>
            <input
                type="text"
                className="form-control"
                autoComplete="off"
                id="search-field"
                onKeyDown={onKeyDown}
                value={searchInput}
                onChange={(e) => handleInput(e)}
                placeholder='Vul hier de plaatsnaam in'
            />
            {showSuggestions && searchInput &&
                <SuggestionsListComponent
                    filteredSuggestions={filteredSuggestions}
                    activeSuggestionIndex={activeSuggestionIndex}
                    onClick={s => setSuggestion(s)}
                    searchInput={searchInput}
                />}
        </>
    );
}
