import './App.css';
import { SearchComponent } from './components/SearchComponent'
import { ListOfEntries } from './components/ListOfEntries';
import { useState, useMemo } from 'react';
import { v4 as uuidv4 } from 'uuid';


function App() {

  const [advisorsArray, setAdvisorsArray] = useState([]);


  // only works when the file is in the public folder
  function getData() {
    fetch('/data/advisors_10000--original.csv')
      .then((response) => response.text())
      .then(csv => {

        //split the data based on the line break, and remove the first row
        const advisors = csv.split('\n').slice(1);

        //array to save all the objects in

        advisorsInfos(advisors)
        setAdvisorsArray(advisorsArray);
        return advisorsInfos;
      });

  }



  const advisorsInfos = useMemo((advisors) => {
    const advisorsArray = [];
    advisors.forEach(element => {
      const advisorData = element.split(';')
      let advisor = {
        id: uuidv4(),
        Bedrijfsnaam: advisorData[0],
        Adres: advisorData[1],
        Postcode: advisorData[2],
        Woonplaats: advisorData[3],
        Website: advisorData[4]
      }
      advisorsArray.push(advisor);
    })
    return advisorsInfos;
  }, [])

  getData()




  //manually parsing the code
  // D3 and P5.js are libraries that parse for you

  //the thing is now a text string, we can take the text and split it into different elements of an array
  //first split the rows, then split the columns. 

  //The thing that separates the things is a line break.


  return (
    <div className="App">
      {/* <button onClick={() => getData()}>Getdata</button> */}
      <SearchComponent />
      {/* <ListOfEntries advisorsInfo={advisorsInfo} /> */}
    </div>
  );
}

export default App;
