import { useState } from "react";
import { Autocomplete } from '../components/Autocomplete';

export const SearchComponent = ({ setSearchQuery, setResultsVisible, suggestions, setShowSuggestions, onClick }) => {
    // const [searchInput, setSearchInput] = useState("");

    const [searchInput, setSearchInput] = useState("");



    const handleSubmit = (e) => {
        e.preventDefault();
        setSearchQuery(searchInput)
        setResultsVisible(true)
        // console.log(`handleSubmit: ${searchInput}`);
    }

    // const inputValue = (userInput) => {
    //     setSearchInput(userInput)
    //     // console.log(`user input: ${userInput}`);
    // }

    function callBack(_searchInput) {
        console.log("hallo");
        setSearchInput(_searchInput);
    }





    return (
        <section id="search-component">
            <form id="search-form" onSubmit={value => handleSubmit(value)}>
                <div className="mb-3 d-flex w-100">
                    <label htmlFor="exampleInputEmail1search-field" className="form-label">
                        <Autocomplete
                            suggestions={suggestions}
                            // inputValue={userInput => inputValue(userInput)}
                            // inputValue={searchInput}
                            // searchInput={searchInput}
                            // SubmitButtonClicked={() => console.log("hoi test")}
                            callBack={callBack}
                            setShowSuggestions={(e) => setShowSuggestions(e)}
                        />
                    </label>
                    <button
                        onClick={() => onClick()}
                        type="submit"
                        value="Submit"
                        className={`btn btn-dark ${(!searchInput) ? 'disabled' : ''}`}>
                        Zoek adviseurs
                    </button>
                </div>
            </form>
        </section >
    )
}
