import './App.css';
import { SearchComponent } from './components/SearchComponent'
import { ListOfEntries } from './components/ListOfEntries';
import { useState } from 'react';



function App() {

  const [advisorsArray, setAdvisorsArray] = useState([]);


  // only works when the file is in the public folder
  function getData() {
    fetch('/data/advisors_10000.csv')
      .then((response) => response.text())
      .then(csv => {

        //split the data based on the line break, and remove the first row
        const advisors = csv.split('\n').slice(1);

        //split the rows into each one of the fields
        advisors.forEach(advisor => {
          const advisorData = advisor.split(';')
          console.log(advisorData);
          setAdvisorsArray(advisorData)
        });
      })
  }


  //manually parsing the code
  // D3 and P5.js are libraries that parse for you

  //the thing is now a text string, we can take the text and split it into different elements of an array
  //first split the rows, then split the columns. 

  //The thing that separates the things is a line break.







  return (
    <div className="App">
      <p>{advisorsArray[1]}</p>
      <button onClick={() => getData()}>Getdata</button>
      <SearchComponent />
      {/* <ListOfEntries data={data} /> */}
    </div>
  );
}

export default App;



