import { useState, useEffect } from 'react';

//components
import { Advisor } from "./Advisor";
import { Loader } from "./Loader";


export const Advisors = ({ currentAdvisors, loading }) => {
    const [tableTitles, setTableTitles] = useState([]);

    //Set the titles of the table with the values of the titlesArray
    useEffect(() => {
        let titlesArray = ["Bedrijfsnaam", "Adres", "Website"];
        setTableTitles(titlesArray)
    }, []);


    const [searchResults, setSearchResults] = useState();

    useEffect(() => {
        if (currentAdvisors.length >= 1) {
            console.log('current advisors true:');
            console.log(currentAdvisors.length);
            setSearchResults(true)

        } else {
            console.log('current advisors false:');
            console.log(currentAdvisors.length);
            setSearchResults(false)
        }
        console.log(searchResults);
    }, [currentAdvisors]);




    return (
        <section id="advisors">
            <Loader loading={loading} />
            <table>
                <thead>
                    <tr>
                        {tableTitles.map(function (columnTitle) {
                            return (
                                <th key={columnTitle}>{columnTitle}</th>
                            )
                        })}
                    </tr>
                </thead>
                <tbody>

                    {currentAdvisors.map(function (currentAdvisor) {
                        return (
                            <Advisor key={currentAdvisor.id} advisor={currentAdvisor} />
                        )
                    })}
                </tbody>
            </table>
        </section>
    )
}
