import { useState, useEffect, useMemo } from 'react';
import { v4 as uuidv4 } from 'uuid';
import './App.css';

//components
import { SearchResults } from './components/SearchResults';
import { SearchComponent } from './components/SearchComponent';
import { Autocomplete } from './components/Autocomplete';



function App() {

  //default
  const [advisorsArray, setAdvisorsArray] = useState([]);

  //search
  const [searchQuery, setSearchQuery] = useState("");
  // const [filteredAdvisors, setFilteredAvisors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [resultsVisible, setResultsVisible] = useState(false);

  //pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [advisorsPerPage] = useState(10);

  //get data from csv file and add it to the advisorArray
  useEffect(() => {
    // only works when the file is in the public folder
    const getData = async () => {
      setLoading(true)

      fetch('/data/advisors_10000--original.csv')
        .then((response) => response.text())
        .then(csv => {

          //get advisors > split the rows into each one of the fields
          const advisors = csv.split('\n').slice(1);
          const tempArray = [];

          //save every advisor and add it to the advisorsArray
          advisors.forEach(element => {
            const advisorData = element.split(';')
            let advisor = {
              id: uuidv4(),
              Bedrijfsnaam: advisorData[0],
              // Adres: `${advisorData[1]}, ${advisorData[2]}, ${advisorData[3]}`,
              Adres: {
                Straatnaam: advisorData[1],
                //remove spces from postcode
                Postcode: advisorData[2].replace(/\s+/g, ''),
                Woonplaats: advisorData[3],

              },
              Website: {
                Naam: advisorData[4].replace("https://www.", '').replace("http://www.", ''),
                Url: advisorData[4],
              }
            }
            tempArray.push(advisor)
          });
          setAdvisorsArray(tempArray)
        })

      setLoading(false)
    }
    getData()
  }, []);


  //Filter the advisorsArray based on the searchQuery
  // useEffect(() => {
  //   function filterAdvisors() {
  //     const newArr = [];
  //     advisorsArray.forEach(advisor => {
  //       if (
  //         advisor.Bedrijfsnaam.toLowerCase()
  //           .includes(searchQuery.toLowerCase())
  //         ||
  //         advisor.Adres.Woonplaats.toLowerCase()
  //           .includes(searchQuery.toLowerCase())) {
  //         newArr.push(advisor)
  //       }
  //       return newArr
  //     });
  //     setFilteredAvisors(newArr)
  //     return filteredAdvisors
  //   }
  //   filterAdvisors()
  // }, [searchQuery]);


  const filteredAdvisors = useMemo(() => {
    const newArr = [];
    advisorsArray.forEach(advisor => {
      if (
        advisor.Bedrijfsnaam.toLowerCase()
          .includes(searchQuery.toLowerCase())
        ||
        advisor.Adres.Woonplaats.toLowerCase()
          .includes(searchQuery.toLowerCase())) {
        newArr.push(advisor)
      }
    });
    return newArr
  }, [advisorsArray, searchQuery]);


  //Pagination variables
  const indexOfLastAdvisor = useMemo(() => {
    return currentPage * advisorsPerPage;
  }, [currentPage, advisorsPerPage])

  const indexOfFirstAdvisor = useMemo(() => {
    return indexOfLastAdvisor - advisorsPerPage;
  }, [indexOfLastAdvisor, advisorsPerPage])

  const currentAdvisors = useMemo(() => {
    return filteredAdvisors.slice(indexOfFirstAdvisor, indexOfLastAdvisor)
  }, [filteredAdvisors, indexOfFirstAdvisor, indexOfLastAdvisor])



  //Set the current page number
  const paginate = (e, pageNumber) => {
    e.preventDefault();
    setCurrentPage(pageNumber)
  }

  const suggestions = () => {
    const possibleSuggestions = [];
    advisorsArray.forEach(advisor => {
      console.log(advisor.Woonplaats);
    })
  }

  useEffect(() => {
    suggestions()
  }, []);


  return (
    <div className="App">
      <Autocomplete
        suggestions={[
          "Alligator",
          "Bask",
          "Crocodilian",
          "Death Roll",
          "Eggs",
          "Jaws",
          "Reptile",
          "Solitary",
          "Tail",
          "Wetlands"
        ]}
      />
      <SearchComponent
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
        setResultsVisible={setResultsVisible}
      />
      <SearchResults
        resultsVisible={resultsVisible}
        loading={loading}
        searchQuery={searchQuery}
        currentAdvisors={currentAdvisors}
        advisorsArray={advisorsArray}
        currentPage={currentPage}
        advisorsPerPage={advisorsPerPage}
        fitleredAdvisors={filteredAdvisors.length}
        paginate={paginate}
      />
    </div>
  );
}

export default App;
