import { useState } from "react";

export const SearchComponent = ({ setSearchQuery, setResultsVisible }) => {
    const [seachInput, setSearchInput] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        setSearchQuery(seachInput)
        setResultsVisible(true)
    }

    const resetFilter = () => {
        setSearchQuery("")
        setSearchInput("")
        setResultsVisible(false)
    }

    return (
        <section id="search-component">
            <form id="search-form" onSubmit={value => handleSubmit(value)}>
                <label>
                    <input
                        required
                        placeholder="Zoek op bedrijfsnaam of woonplaats"
                        value={seachInput}
                        onChange={e => setSearchInput(e.target.value)}
                    />
                </label>
                <button
                    type="submit"
                    value="Submit">
                    Zoek
                </button>
                <button
                    onClick={() => resetFilter()}>
                    Reset filter
                </button>
            </form>
        </section >
    )
}
