import './App.css';
import { SearchComponent } from './components/SearchComponent'
import data from './data/data.json';
import { ListOfEntries } from './components/ListOfEntries';
import { useState } from 'react';

import DATA from './data/advisors_10000.csv';
const DATA_ARRAY = [];

function App() {

  const [dataArray, setDataArray] = useState([]);


  //only works when the file is in the public folder
  // function getData() {
  //   fetch('/data/advisors_10000.csv')
  //     .then((response) => response.text())
  //     .then(csv => {
  //       console.log(csv);
  //     })
  // }
  // getData()




  async function getData() {
    const response = await fetch(DATA, {
      headers: {
        'content-type': 'text/csv;charset=UTF-8'
      }
    })
      .catch(function () {
        console.log("error");
      });


    const data = await response.text();
    // console.log(data);

    const rows = data.split((/\r?\n|\r/)).slice(1);
    // console.log(rows[1]);
    rows.forEach(elt => {
      const colums = elt.split(';');
      const titre = colums[0];
      // console.log(titre);
      DATA_ARRAY.push(titre);

    })

    console.log(DATA_ARRAY);
  }

  getData();


  return (
    <div className="App">
      <SearchComponent />
      <ListOfEntries data={data} />
    </div>
  );
}

export default App;
