export const Pagination = ({advisorsPerPage, totalAdvisors}) => {
    const pageNumbers = [];

    //get the amount of page numbers
    for (let i = 1; i <= Math.ceil(totalAdvisors / advisorsPerPage); i++){
    pageNumbers.push(i)
    }
    console.log(pageNumbers);

    return (
        <div id="pagination">
            <ul className="pagination">
                {pageNumbers.map(number =>(
                    <li key={number} className="pagination__page-item">
                        <a href="!#" className="pagination__page-link">
                            {number}
                        </a>
                    </li>
                ))}
            </ul>
        </div>
    )
}
