            const rawAliasses = csv.split('\n');
            // console.log(rawAliasses);
            const parsedAliasses = [];

            rawAliasses.forEach(element => {
                const aliasData = element.split(',')

                let alias = {
                    name1: aliasData[0],
                    name2: aliasData[1]
                }
                parsedAliasses.push(alias)
            })
            console.log(parsedAliasses);
            console.log(`-------------`);
            aliasCollection(parsedAliasses)

        })
}



const aliasCollection = (parsedAliasses) => {
    console.log(`aliasCollection`);


    function checkSameCity(alias1, alias2) {
        console.log(alias1.name1);
        console.log(alias2.name2);

        if (!alias1.name1.includes(alias2.name2)) {
            return false;
        }
        return true;
    }
    console.log(checkSameCity(parsedAliasses[1], parsedAliasses[1]));

}





