import { useState } from "react";
import { Autocomplete } from '../components/Autocomplete';

export const SearchComponent = ({ setSearchQuery, setResultsVisible, suggestions }) => {
    const [seachInput, setSearchInput] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        setSearchQuery(seachInput)
        setResultsVisible(true)
    }

    const handleInputTest = (value) => {
        setSearchInput(value)
    }

    return (
        <section id="search-component">
            <form id="search-form" onSubmit={value => handleSubmit(value)}>
                <label>
                    <Autocomplete
                        suggestions={suggestions}
                        inputTest={i => handleInputTest(value)}
                        seachInput={seachInput}

                    />
                    <input
                        required
                        placeholder="Zoek op bedrijfsnaam of woonplaats"
                        value={seachInput}
                        onChange={e => setSearchInput(e.target.value)}
                    />
                </label>
                <button
                    type="submit"
                    value="Submit">
                    Zoek
                </button>
            </form>
        </section >
    )
}
