            //push it to the setSuggestion()
            setSuggestion(suggestion)


        } else if (e.keyCode === 38) {
            //on arrow up
            if (activeSuggestionIndex <= 0) {
                //do nothing
                return
            } if (activeSuggestionIndex > 0) {
                //increment index with one
                setActiveSuggestionIndex(activeSuggestionIndex - 1)
            }
        } else if (e.keyCode === 40) {
            //on  arrow down
            if (activeSuggestionIndex <= filteredSuggestions.length - 2) {
                //increment index with one
                setActiveSuggestionIndex(activeSuggestionIndex + 1)
            }
        }
    }

    return (
        <section id="search-component">
            <form id="search-form"
                onSubmit={value => handleSubmit(value)
                }>
                <div className="mb-3 d-flex w-100">
                    <label
                        htmlFor="exampleInputEmail1search-field"
                        className="form-label"
                    >
                        <AutoComplete
                            suggestions={suggestions}
                            onSearchTerm={setSearchTerm}
                            setShowSuggestions={(e) => setShowSuggestions(e)}
                        />
                    </label>
                    <button
                        type="submit"
                        value="Submit"
                        className={`btn btn-dark 
                            ${(!searchTerm) ? 'disabled' : ''}`}>
                        Zoek adviseurs
                    </button>
                </div>
            </form>
        </section >
    )
}
