import { useMemo } from "react";

export const Pagination = ({ advisorsPerPage, totalAdvisors, paginate, currentPage }) => {
    const pageNumbers = [];

    //get the amount of page numbers
    useEffect(() => {
        for (let i = 1; i <= Math.ceil(totalAdvisors / advisorsPerPage); i++) {
            pageNumbers.push(i)
            console.log(totalAdvisors);
        }
    }, []);






    return (
        <div id="pagination">
            <ul className="pagination">
                {pageNumbers.map(number => (
                    <li key={number} className="pagination__page-item">
                        <a href="!#"
                            className={`pagination__page-link 
                                ${currentPage === number ? "active" : ""}
                            `}
                            onClick={() => paginate(number)}>
                            {number}
                        </a>
                    </li>
                ))}
            </ul>
        </div>
    )
}
