import { Advisor } from "./Advisor";
import { Loader } from "./Loader";
import { useState, useEffect } from 'react';

export const Advisors = ({ currentAdvisors, searchQuery, loading, advisorsArray }) => {
    const [tableTitles, setTableTitles] = useState([]);


    useEffect(() => {
        let titlesArray = ["Bedrijfsnaam", "Adres", "Website"];
        setTableTitles(titlesArray)
    }, []);

    let dataSource;

    useEffect((currentAdvisors, advisorsArray) => {
        // if (!searchQuery) {
        //     console.log('no');
        //     dataSource = currentAdvisors;
        // } else {
        //     console.log('yes');
        //     dataSource = advisorsArray;
        // }

    }, []);

    console.log(currentAdvisors[0]);

    return (
        <section id="advisors">
            <Loader loading={loading} />
            <table>
                <thead>
                    <tr>
                        {tableTitles.map(function (columnTitle) {
                            return (
                                <th key={columnTitle}>{columnTitle}</th>
                            )
                        })}
                    </tr>
                </thead>
                <tbody>
                    {currentAdvisors.filter(advisor =>
                        advisor.Bedrijfsnaam.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        advisor.Adres.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        advisor.Website.toLowerCase().includes(searchQuery.toLowerCase())
                    ).map(filteredAdvisor => (
                        <Advisor key={filteredAdvisor.id} entry={filteredAdvisor} />
                    ))}
                </tbody>
        </table>
        </section >
    )
}
