import { useState } from 'react';

export const Autocomplete = ({ suggestions }) => {

    const [filteredSuggestions, setFilteredSuggestions] = useState([]);
    const [activeSuggestionsIndex, setActiveSuggestionsIndex] = useState(0);
    // const [showSuggestions, setShowSuggestions] = useState(false);
    const [input, setInput] = useState("");


    const onChange = (e) => {
        // console.log('onchange function');
        const userInput = e.target.value;
        // console.log(userInput);

        // Filter our suggestions that don't contain the user's input
        const unLinked = suggestions.filter(
            (suggestion) =>
                suggestion.toLowerCase().indexOf(userInput.toLowerCase()) > -1
        );
        console.log(unLinked);
        setInput(e.target.value);
        setFilteredSuggestions(unLinked)
        setActiveSuggestionsIndex(0)
        setShowSuggestions(true)
    }
    return (
        <>
            <p>input: {input}</p>
            <p>filteredSuggestions: {filteredSuggestions}</p>
            <p>activeSuggestionsIndex: {activeSuggestionsIndex}</p>
            <p>showSuggestions: {showSuggestions}</p>
            <input
                onChange={(e) => onChange(e)} type="text" placeholder="type a name"
            />
        </>
    )
}
