import { useState } from 'react';

export const FilterTest = ({ advisorsArray }) => {

    const [testInputSearch, setTestInputSearch] = useState("");
    const [testSearchQuery, setTestSearchQuery] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        setTestSearchQuery(testInputSearch)
        console.log(advisorsArray);

    }

    return (
        <>
            <div>
                <p>Dit is de testInputSearch: {testInputSearch}</p>
                <p>Dit is de searchQuery: {testSearchQuery}</p>
                <form id="search-form" onSubmit={value => handleSubmit(value)}>
                    <input type="text" value={testInputSearch}
                        onChange={e => setTestInputSearch(e.target.value)} />
                </form>
            </div>
            <div>
                {advisorsArray.filter(advisor => advisor.Woonplaats === testSearchQuery).map(filteredPerson => (
                    <li>
                        {filteredPerson.Woonplaats}
                    </li>
                ))}
            </div>
        </>

    )
}
