import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons'
import { PaginationPage } from "../components/PaginationPage";

export const Pagination = ({
    advisorsPerPage,
    totalAdvisors,
    currentPageNumber,
    currentPage,
    maxAmountPaginationPages }) => {
    const pageNumbers = [];

    //get the amount of page numbers
    for (let i = 1; i <= Math.ceil(totalAdvisors / advisorsPerPage); i++) {
        pageNumbers.push(i)
    }

    const paginate = (e, number) => {
        e.preventDefault()
        currentPageNumber(number)
    }

    const nextPage = () => {
        currentPageNumber(currentPage + 1)
    }

    const prevPage = () => {
        currentPageNumber(currentPage - 1)
    }

    const firstPage = () => {
        return pageNumbers[pageNumbers.length - pageNumbers.length]
    }

    const lastPage = () => {
        return pageNumbers[pageNumbers.length - 1]
    }

    const test = () => {
        console.log(maxAmountPaginationPages);
    }

    const visiblePageNumbers = () => {
        console.log(maxAmountPaginationPages);
    }


    return (
        <>
            <button onClick={() => test()}>test</button>
            <nav aria-label="Page navigation">
                <ul className="pagination justify-content-center">
                    <li className={`page-item page-item--first ${((currentPage === firstPage()) ? 'disabled opacity-50' : '')}`}>
                        <a className="page-link" href="#next" onClick={(e) => paginate(e, firstPage)}>Eerste</a>
                    </li>
                    <li className={`page-item page-item--prev ${(currentPage === 1) ? 'disabled opacity-50' : ''}`}>
                        <a className="page-link" href="#prev" onClick={() => prevPage()}>
                            <FontAwesomeIcon className="icon" icon={faArrowLeft} />
                        </a>
                    </li>
                    <div className='page-item-group d-flex'>
                        <li className={`page-item disabled ${(currentPage === 1) ? 'opacity-0' : ''}`}>
                            <div className="page-link" href="#next" >
                                ...
                            </div>
                        </li>
                        <PaginationPage
                            pageNumbers={pageNumbers}
                            currentPage={currentPage}
                            paginate={p => paginate(p)}
                            maxAmountPaginationPages={maxAmountPaginationPages}
                        />
                        <li className={`page-item disabled ${(currentPage === lastPage()) ? 'opacity-0' : ''}`}>
                            <div className="page-link" href="#next" >
                                ...
                            </div>
                        </li>
                    </div>
                    <li className={`page-item page-item--next ${(currentPage === (pageNumbers[pageNumbers.length - 1]) ? 'disabled opacity-50' : '')}`}>
                        <a className="page-link" href="#next" onClick={() => nextPage()} >
                            <FontAwesomeIcon className="icon" icon={faArrowRight} />
                        </a>
                    </li>
                    <li className={`page-item page-item--last ${((currentPage === lastPage()) ? 'disabled opacity-50' : '')}`}>
                        <a className="page-link" href="#next" onClick={(e) => paginate(e, lastPage)} >Laatste</a>
                    </li>
                </ul>
            </nav>









            {/* <button onClick={() => test()}>test</button> */}
            {/* Mijn oplossing */}
            {/* <div id="pagination">
                <ul className="pagination">
                    {pageNumbers.map(number => (
                        <li key={number} className="page-item">
                            <a href="!#"
                                className={`page-link 
                                ${currentPage === number ? "active" : ""}`}
                                onClick={(e) => paginate(e, number)}>
                                {number}
                            </a>
                        </li>
                    ))}
                </ul>
            </div> */}
        </>
        // Bootstrap verision

        //         <nav aria-label="Page navigation example">
        //     <ul class="pagination justify-content-center">
        //         <li class="page-item disabled">
        //             <a class="page-link">Previous</a>
        //         </li>
        //         <li class="page-item"><a class="page-link" href="#">1</a></li>
        //         <li class="page-item"><a class="page-link" href="#">2</a></li>
