import { useState } from "react";
import { Autocomplete } from '../components/Autocomplete';

export const SearchComponent = ({ setSearchQuery, setResultsVisible, suggestions, setShowSuggestions, suggestion }) => {

    const [searchInput, setSearchInput] = useState("");
    // const [suggestion, setSuggestion] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        setSearchQuery(searchInput)
        setResultsVisible(true)

        // console.log(`form submitted ${searchInput}`);
        // console.log(`form submitted ${searchTerm}`);
        console.log(suggestion);
    }

    // const selectedSuggestion = (suggestion) => {
    //     setSuggestion(suggestion)
    //     console.log(`set suggestion with ${suggestion}`);
    // }

    //get the 
    const searchTerm = (inputFieldValue) => {
        setSearchInput(inputFieldValue);
        console.log(`serchComponent searchInput inputFieldValue: ${inputFieldValue}`);
    }


    return (
        <section id="search-component">
            <form id="search-form"
                selectedSuggestion={selectedSuggestion}
                onSubmit={value => handleSubmit(value)
                }>
                <div className="mb-3 d-flex w-100">
                    <label htmlFor="exampleInputEmail1search-field" className="form-label">
                        <Autocomplete
                            suggestions={suggestions}
                            searchTerm={searchTerm}
                            setShowSuggestions={(e) => setShowSuggestions(e)}
                        />
                    </label>
                    <button
                        type="submit"
                        value="Submit"
                        className={`btn btn-dark ${(!searchInput) ? 'disabled' : ''}`}>
                        Zoek adviseurs
                    </button>
                </div>
            </form>
        </section >
    )
}
