import { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import './App.css';

//components
import { SearchResults } from './components/SearchResults';
import { SearchComponent } from './components/SearchComponent';


function App() {

  //default
  const [advisorsArray, setAdvisorsArray] = useState([]);

  //search
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredAdvisors, setFilteredAvisors] = useState([]);
  const [loading, setLoading] = useState(true);

  //pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [advisorsPerPage] = useState(50);


  //get data from csv file and add it to the advisorArray
  useEffect(() => {
    // only works when the file is in the public folder
    const getData = async () => {
      setLoading(true)

      fetch('/data/advisors_10000--original.csv')
        .then((response) => response.text())
        .then(csv => {

          //get advisors > split the rows into each one of the fields
          const advisors = csv.split('\n').slice(1);
          const advisorsArray = [];

          //save every advisor and add it to the advisorsArray
          advisors.forEach(element => {
            const advisorData = element.split(';')
            let advisor = {
              id: uuidv4(),
              Bedrijfsnaam: advisorData[0],
              Adres: `${advisorData[1]}, ${advisorData[2]}, ${advisorData[3]}`,
              Website: advisorData[4]
            }
            advisorsArray.push(advisor)
          });
          setAdvisorsArray(advisorsArray)
        })
      setLoading(false)
    }
    getData()
  }, []);


  //Filter the advisorsArray based on the searchQuery
  // useEffect(() => {
  function filterAdvisors() {
    const newArr = [];
    advisorsArray.forEach(advisor => {
      if (
        advisor.Bedrijfsnaam.toLowerCase()
          .includes(searchQuery.toLowerCase())
        ||
        advisor.Adres.toLowerCase()
          .includes(searchQuery.toLowerCase())) {
        newArr.push(advisor)
      }
      return newArr
    });
    setFilteredAvisors(newArr)
    return filteredAdvisors
  }
  //   filterAdvisors()
  //   console.log('hoevaak wordt dit gerenderd?');
  // }, []);

  //Pagination variables
  const indexOfLastAdvisor = currentPage * advisorsPerPage;
  const indexOfFirstAdvisor = indexOfLastAdvisor - advisorsPerPage;
  const currentAdvisors = filteredAdvisors.slice(indexOfFirstAdvisor, indexOfLastAdvisor)

  //Set the current page number
  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber)
  }

  return (
    <div className="App">
      <SearchComponent
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
      />
      <SearchResults
        loading={loading}
        searchQuery={searchQuery}
        currentAdvisors={currentAdvisors}
        advisorsArray={advisorsArray}
        currentPage={currentPage}
        advisorsPerPage={advisorsPerPage}
        fitleredAdvisors={filteredAdvisors.length}
        paginate={paginate}
      />
    </div>
  );
}

export default App;
