import { Advisor } from "./Advisor";
import { Loader } from "./Loader";
import { useState } from 'react';

export const Advisors = ({ advisorsArray, searchQuery, loading }) => {

    useEffect(() => {
  const [tableTitles, setTableTitles] = useState([]);

    let titlesArray = [];
    setTableTitles(titlesArray)
  }, []);


  return (
        <section id="advisors">
            <Loader loading={loading} />
            <table>
                <thead>
                    <tr>
                        {tableTitles.map(function (columnTitle) {
                            return (
                                <th key={columnTitle}>{columnTitle}</th>
                            )
                        })}
                    </tr>
                </thead>
                <tbody>
                    {advisorsArray.filter(advisor =>
                        advisor.Bedrijfsnaam.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        advisor.Adres.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        advisor.Website.toLowerCase().includes(searchQuery.toLowerCase())
                    ).map(filteredAdvisor => (
                        <Advisor key={filteredAdvisor.id} entry={filteredAdvisor} />
                    ))}
                </tbody>
            </table >
        </section >
    )
}
