import './App.css';
import { SearchComponent } from './components/SearchComponent';
import { Advisors } from './components/Advisors';
import { Pagination } from './components/Pagination';
import { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';

function App() {

  //Advisors
  const [advisorsArray, setAdvisorsArray] = useState([]);

  const [loading, setLoading] = useState(true);

  //Search function
  const [searchQuery, setSearchQuery] = useState("");

  //Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [advisorsPerPage] = useState(10);

  // const [isSeachResult, setIsSearchResult] = useState(false);
  const [filteredAdvisors, setFilteredAvisors] = useState([]);

  useEffect(() => {
    // only works when the file is in the public folder
    const getData = async () => {
      setLoading(true)
      fetch('/data/advisors_10000--original.csv')
        .then((response) => response.text())
        .then(csv => {

          //getting the advisors from the cv file
          //and split the rows into each one of the fields
          const advisors = csv.split('\n').slice(1);
          const advisorsArray = [];

          advisors.forEach(element => {
            const advisorData = element.split(';')
            let advisor = {
              id: uuidv4(),
              Bedrijfsnaam: advisorData[0],
              Adres: `${advisorData[1]}, ${advisorData[2]}, ${advisorData[3]}`,
              Website: advisorData[4]
            }
            advisorsArray.push(advisor)
          });
          setAdvisorsArray(advisorsArray)
        })
      setLoading(false)
    }

    getData()
  }, [loading]);

  useEffect((searchQuery) => {
    const testQuery = "Mulder NV";
    function filterAdvisors(advisorsArray) {
      const newArr = [];
      advisorsArray.forEach(advisor => {
        if (advisor.Bedrijfsnaam.toLowerCase().includes(searchQuery.toLowerCase()) ||
          advisor.Adres.toLowerCase().includes(searchQuery.toLowerCase()) ||
          advisor.Website.toLowerCase().includes(searchQuery.toLowerCase())) {
          newArr.push(advisor)
        }
        return newArr
      });
      setFilteredAvisors(newArr)
      return filteredAdvisors
    }
  }, [searchQuery]);

  //get current advisors for pagination
  const indexOfLastAdvisor = currentPage * advisorsPerPage;
  const indexOfFirstAdvisor = indexOfLastAdvisor - advisorsPerPage;
  const currentAdvisors = filteredAdvisors.slice(indexOfFirstAdvisor, indexOfLastAdvisor)

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber)
  }

  return (
    <div className="App">
      {/* <FilterTest setTestSearchQuery={setTestSearchQuery} testSearchQuer={testSearchQuery} advisorsArray={advisorsArray} /> */}
      {/* <button onClick={() => filterAdvisors(advisorsArray)}>testbutton</button> */}
      <SearchComponent searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
      <Advisors
        loading={loading}
        searchQuery={searchQuery}
        //currentAdvisors comes from the pagination
        currentAdvisors={currentAdvisors}
        advisorsArray={advisorsArray}
        currentPage={currentPage}
        advisorsPerPage={advisorsPerPage} />
      <Pagination currentPage={currentPage} advisorsPerPage={advisorsPerPage} totalAdvisors={filteredAdvisors.length} paginate={paginate} />
    </div>
  );
}

export default App;
