import e from "express";
import { useState } from "react"

export const SearchComponent = () => {
    const [seachInput, setSearchInput] = useState("");
    const [searchQuery, setSearchQuery] = useState("");



    const handleSubmit = (e) => {
        e.preventDefault();
        if (isFirstInvestment()) {
            console.log(`isFirstInvestment is true`);
        } else {
            console.log(`isFirstInvestment is false`);
        }
        const newInvestment = {
            id: uuidv4(),
            investment: parseInt(inputInvestment),
            // totalInvestment: getTotalInvestment(),
            marketPrice: parseInt(inputMarketPrice),
            btc: parseFloat(inputBTC),
            createdAt: getDate(),
            // value: getValue(parseInt(inputMarketPrice), inputBTC),
            // // gain: ((inputBTC) * parseInt(inputMarketPrice) - (parseInt(inputInvestment) + parseInt(getLastInvestmentAmount())))
            // gainValue: getGainValue(),
            // gainPercentage: getGainPercentage()
        }

        onSubmit(newInvestment)
    }


    return (
        <section id="search-component">
            <p>{setSearchInput}</p>
            <p>{searchQuery}</p>
            <form id="search-form" onSubmit={value => handleSubmit(value)}>
                <label>
                    Zoek een adviseur:
                    <input required
                        placeholder="Zoek op naam"
                        value={seachInput}
                        onChange={e => setSearchInput(e.target.value)} />
                </label>
                <button type="submit" value="Submit">submit</button>
            </form>
        </section >
    )
}
