import { v4 as uuidv4 } from 'uuid';


export const fetchAdvisors = () => {
    return fetch('/data/advisors_10000--original.csv')
        .then((response) => response.text())
        .then(csv => {

            //get advisors > split the rows into each one of the fields
            const rawAdvisors = csv.split('\n').slice(1);
            const parsedAdvisors = [];

            //save every advisor and add it to the advisorsArray
            rawAdvisors.forEach(element => {
                const advisorData = element.split(';')
                let advisor = {
                    id: uuidv4(),
                    Bedrijfsnaam: advisorData[0],
                    // Adres: `${advisorData[1]}, ${advisorData[2]}, ${advisorData[3]}`,
                    Adres: {
                        Straatnaam: advisorData[1],
                        //remove spces from postcode
                        Postcode: advisorData[2].replace(/\s+/g, ''),
                        Woonplaats: advisorData[3],

                    },
                    Website: {
                        Naam: advisorData[4].replace("https://www.", '').replace("http://www.", ''),
                        Url: advisorData[4],
                    }
                }
                parsedAdvisors.push(advisor)
            });
            // console.log(parsedAdvisors);
            return parsedAdvisors;
        })
}


export const fetchAliases = () => {
    return fetch('/data/aliasses_2021-12-06-075438_helz.csv')
        .then((response) => response.text())
        .then(csv => {

            const rawAliasses = csv.split('\n');
            const parsedAliasses = [];

            rawAliasses.forEach(element => {
                const aliasData = element.split(',')

                let alias = {
                    name0: aliasData[0],
                    name1: aliasData[1]
                }
                parsedAliasses.push(alias)
            });
            // console.log(parsedAliasses);
            aliasCollection(parsedAliasses)
            return parsedAliasses;
        })
}



const aliasCollection = (parsedAliasses) => {

    const checkSamePlace = (alias1, alias2) => {
        if (alias1.name0.includes(alias2.name0 || alias2.name1)
            || alias1.name1.includes(alias2.name0 || alias2.name1)) {
            return true;
        }
        return false;
    }

    //hierin bewaren we arrays die per array alle aliassen bevatten van 1 stad
    const allAliases = [];

    //voor elke alias loopen we door deze functie
    for (let i = 0; i < 10 - 1; i++) {
        // console.log(parsedAliasses.indexOf(parsedAliasses[i]));
        //voor de huidige alias wordt er een array aangemaakt
        let oneCityArray = [];

        //de waardes van de huidige alias worden aan de oneCity array toegevoegd
        oneCityArray.push(parsedAliasses[i].name0)
        oneCityArray.push(parsedAliasses[i].name1)

        //Als de huidige alias, en de daarop volgende alias dezelfde waarden bevatten
        //voegen we de waarden van de tweede rij ook aan de oneCity array toe
        if (checkSamePlace(parsedAliasses[i], parsedAliasses[i + 1])) {
            console.log(parsedAliasses[i].includes);
