import { useState } from "react"

export const SearchComponent = ({ setSearchQuery }) => {
    const [seachInput, setSearchInput] = useState("");


    const addedSearchQuery = true;

    const handleSubmit = (e) => {
        e.preventDefault();
        setSearchQuery(seachInput)
    }

    const resetFilter = () => {
        setSearchQuery("")
        setSearchInput("")
    }

    return (
        <section id="search-component">
            <form id="search-form" onSubmit={value => handleSubmit(value)}>
                <label>
                    {addedSearchQuery ? 'Zoek een adviseur:' : 'Zoek nog een keer een adviseur'}
                    <input required
                        placeholder="Zoek op woonplaats"
                        value={seachInput}
                        onChange={e => setSearchInput(e.target.value)} />
                </label>
                <button type="submit" value="Submit">Filter</button>
                <button onClick={() => resetFilter()}>Reset filter</button>
            </form>

        </section >
    )
}
