import { Advisor } from "./Advisor";
import { Loader } from "./Loader";
import { useState, useEffect } from 'react';

export const Advisors = ({ currentAdvisors, searchQuery, loading }) => {
    const [tableTitles, setTableTitles] = useState([]);

    useEffect(() => {
        let titlesArray = ["Bedrijfsnaam", "Adres", "Website"];
        setTableTitles(titlesArray)
    }, []);


    return (
        <section id="advisors">
            <Loader loading={loading} />
            <table>
                <thead>
                    <tr>
                        {tableTitles.map(function (columnTitle) {
                            return (
                                <th key={columnTitle}>{columnTitle}</th>
                            )
                        })}
                    </tr>
                </thead>
                <tbody>
                    {currentAdvisors.map(function (advisor) {
                        return (
                            <Advisor key={advisor.id} entry={advisor} />
                        )
                    })}
                </tbody>
            </table>
        </section>
    )
}
