import { Advisor } from "./Advisor";
import { Loader } from "./Loader";

export const Advisors = ({ tableTitles, advisorsArray, searchQuery, loading, currentPage, advisorsPerPage }) => {


    //get current advisors
    const indexOfLastAdvisor = currentPage * advisorsPerPage

    return (
        <section id="advisors">
            <Loader loading={loading} />
            <table>
                <thead>
                    <tr>
                        {tableTitles.map(function (columnTitle) {
                            return (
                                <th key={columnTitle.id}>{columnTitle.title}</th>
                            )
                        })}
                    </tr>
                </thead>
                <tbody>
                    {advisorsArray.filter(advisor =>
                        advisor.Bedrijfsnaam.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        advisor.Adres.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        advisor.Postcode.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        advisor.Woonplaats.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        advisor.Website.toLowerCase().includes(searchQuery.toLowerCase())
                    ).map(filteredAdvisor => (
                        <Advisor key={filteredAdvisor.id} entry={filteredAdvisor} />
                    ))}
                </tbody>
            </table >
        </section >
    )
}
