import './App.css';
import { SearchComponent } from './components/SearchComponent';
import { Advisors } from './components/Advisors';
import { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';

function App() {

  const [advisorsArray, setAdvisorsArray] = useState([]);
  const [tableTitles, setTableTitles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");

  //for pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [advisorsPerPage, setAdvisorsPerPage] = useState(4);

  useEffect(() => {
    // only works when the file is in the public folder
    const getData = async () => {
      setLoading(true)
      console.log(loading);
      fetch('/data/advisors_10000--original.csv')
        .then((response) => response.text())
        .then(csv => {

          //get the titles from the first row of the csv file 
          //for the purpose of creating the titles of the table columns
          const titles = csv.split('\n').slice(0, 1);
          let titlesArray = [];
          titles.forEach(element => {
            const titleElement = element.split(';');
            titleElement.forEach(element => {
              const title = {
                id: uuidv4(),
                title: element
              }
              titlesArray.push(title)
            })
          });
          setTableTitles(titlesArray)


          //getting the advisors from the cv file
          //and split the rows into each one of the fields
          const advisors = csv.split('\n').slice(1);
          const advisorsArray = [];

          advisors.forEach(element => {
            const advisorData = element.split(';')
            let advisor = {
              id: uuidv4(),
              Bedrijfsnaam: advisorData[0],
              Adres: advisorData[1],
              Postcode: advisorData[2],
              Woonplaats: advisorData[3],
              Website: advisorData[4]
            }
            advisorsArray.push(advisor)

          });
          setAdvisorsArray(advisorsArray)
        })
      setLoading(false)
    }

    getData()
  }, [loading]);


  //get current advisors for pagination
  const indexOfLastAdvisor = currentPage * advisorsPerPage;
  const indexOfFirstAdvisor = indexOfLastAdvisor - advisorsPerPage;
  const currentAdvisors = advisorsArray.slice(indexOfFirstAdvisor, indexOfLastAdvisor)




  return (
    <div className="App">
      {/* <FilterTest setTestSearchQuery={setTestSearchQuery} testSearchQuer={testSearchQuery} advisorsArray={advisorsArray} /> */}

      <SearchComponent searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
      <Advisors
        loading={loading}
        searchQuery={searchQuery}
        tableTitles={tableTitles}
        //currentAdvisors comes from the pagination
        advisorsArray={currentAdvisors}
        currentPage={currentPage}
        advisorsPerPage={advisorsPerPage} />
    </div>
  );
}

export default App;
