import { useState, useEffect } from 'react';

//components
import { Advisor } from "./Advisor";


export const Advisors = ({ currentAdvisors }) => {
    const [tableTitles, setTableTitles] = useState([]);

    //Set the titles of the table with the values of the titlesArray
    useEffect(() => {
        let titlesArray = ["Bedrijfsnaam", "Adres", "Website"];
        setTableTitles(titlesArray)
    }, []);


    return (
        <section id="advisors">
            <table className='table table-light'>
                <thead>
                    <tr>
                        {tableTitles.map(function (columnTitle) {
                            return (
                                <th
                                    scope="col"
                                    key={columnTitle}
                                >
                                    {columnTitle}
                                </th>
                            )
                        })}
                    </tr>
                </thead>
                <tbody>
                    {currentAdvisors.map(function (currentAdvisor) {
                        return (
                            <Advisor
                                scope="row"
                                key={currentAdvisor.id}
                                advisor={currentAdvisor}
                            />
                        )
                    })}
                </tbody>
            </table>
        </section>
    )
}
