import { TweenMax } from "gsap/gsap-core";

export const Pagination = ({
    advisorsPerPage,
    totalAdvisors,
    currentPageNumber,
    currentPage }) => {
    const pageNumbers = [];

    //get the amount of page numbers
    for (let i = 1; i <= Math.ceil(totalAdvisors / advisorsPerPage); i++) {
        pageNumbers.push(i)
    }

    const paginate = (e, number) => {
        e.preventDefault()
        currentPageNumber(number)
    }

    const nextPage = () => {
        currentPageNumber(currentPage + 1)
    }

    const prevPage = () => {
        currentPageNumber(currentPage - 1)
    }


    const firstPage = () => {
        return (pageNumbers[pageNumbers.length - pageNumbers.length])

    }

    const lastPage = () => {
        return (pageNumbers[pageNumbers.length - 1])
    }


    const test = () => {
        console.log(lastPage());
    }


    return (
        <>
            <button onClick={() => test()}></button>
            <nav aria-label="Page navigation">
                <ul className="pagination justify-content-center">
                    <li className={`page-item page-item--first ${((firstPage) ? 'disabled' : '')}`}>
                        <a className="page-link" href="#next" onClick={() => firstPage()} >Eerste</a>
                    </li>
                    <li className={`page-item ${(currentPage === 1) ? 'disabled' : ''}`}>
                        <a className="page-link" href="#prev" onClick={() => prevPage()}>Previous</a>
                    </li>
                    {pageNumbers.map((number, i) => {
                        i++;
                        return (
                            <li key={number} className={`page-item ${currentPage === number ? " active" : ""}`}>
                                <a href={`#${i}`} className={`page-link `} onClick={(e) => paginate(e, number)}>{number}</a>
                            </li>
                        )
                    })}
                    <li className={`page-item ${(currentPage === (pageNumbers[pageNumbers.length - 1]) ? 'disabled' : '')}`}>
                        <a className="page-link" href="#next" onClick={() => nextPage()} >Next</a>
                    </li>
                    <li className={`page-item page-item--last ${((currentPage === lastPage) ? 'disabled' : '')}`}>
                        <a className="page-link" href="#next" onClick={() => lastPage()} >Laatste</a>
                    </li>
                </ul>
            </nav>







            {/* <button onClick={() => test()}>test</button> */}
            {/* Mijn oplossing */}
            {/* <div id="pagination">
                <ul className="pagination">
                    {pageNumbers.map(number => (
                        <li key={number} className="page-item">
                            <a href="!#"
                                className={`page-link 
                                ${currentPage === number ? "active" : ""}`}
                                onClick={(e) => paginate(e, number)}>
                                {number}
                            </a>
                        </li>
                    ))}
                </ul>
            </div> */}
        </>
        // Bootstrap verision

        //         <nav aria-label="Page navigation example">
        //     <ul class="pagination justify-content-center">
        //         <li class="page-item disabled">
        //             <a class="page-link">Previous</a>
        //         </li>
        //         <li class="page-item"><a class="page-link" href="#">1</a></li>
        //         <li class="page-item"><a class="page-link" href="#">2</a></li>
        //         <li class="page-item"><a class="page-link" href="#">3</a></li>
        //         <li class="page-item">
        //             <a class="page-link" href="#">Next</a>
        //         </li>
        //     </ul>
        // </nav>
