import { useState } from "react";
import { AutoComplete } from './AutoComplete';

export const SearchComponent = ({
    onSearch,
    setResultsVisible,
    suggestions,
    setShowSuggestions,
    suggestion
}) => {

    const [searchTerm, setSearchTerm] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        onSearch(searchTerm)
        setResultsVisible(true)
        console.log(`handleSubmit`);
        console.log(suggestion);
    }

    const keyDown = (e) => {
        console.log(`test, ${e}`);
        if (e.keyCode === 13) {
            //     //on enter
            //     //get the value of the selected suggestion
            //     const suggestion = filteredSuggestions[activeSuggestionIndex]
            //     //push it to the setSuggestion()
            // setSuggestion(suggestion)
            console.log('pressed enter');


        } else if (e.keyCode === 38) {
            console.log('pressed arrow up');
            //     //on arrow up
            //     if (activeSuggestionIndex <= 0) {
            //         //do nothing
            //         return
            //     } if (activeSuggestionIndex > 0) {
            //         //increment index with one
            //         setActiveSuggestionIndex(activeSuggestionIndex - 1)
            // }
        } else if (e.keyCode === 40) {
            console.log('pressed arrow down');
            //     //on  arrow down
            //     if (activeSuggestionIndex <= filteredSuggestions.length - 2) {
            //         //increment index with one
            //         setActiveSuggestionIndex(activeSuggestionIndex + 1)
        }
    }


    return (
        <section id="search-component">
            <form id="search-form"
                onSubmit={value => handleSubmit(value)
                }>
                <div className="mb-3 d-flex w-100">
                    <label
                        htmlFor="exampleInputEmail1search-field"
                        className="form-label"
                    >
                        <AutoComplete
                            suggestions={suggestions}
                            onSearchTerm={setSearchTerm}
                            setShowSuggestions={(e) => setShowSuggestions(e)}
                            onKeyDown={(e) => keyDown(e)}
                        />
                    </label>
                    <button
                        type="submit"
                        value="Submit"
                        className={`btn btn-dark 
                            ${(!searchTerm) ? 'disabled' : ''}`}>
                        Zoek adviseurs
                    </button>
                </div>
            </form>
        </section >
    )
}
